package week1.day1;

public class ConvertNegativeToPositive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = -40;

		if (num < 0) {
			System.out.println("Its a negative number");
			
		}
		else  {
			System.out.println("Its a positive number");
		}
	}

}
